document.addEventListener('DOMContentLoaded', function () { const hamburger = document.querySelector('.hamburger'); const navLinks = document.querySelector('.nav-links'); if (hamburger) { hamburger.addEventListener('click', function () { navLinks.classList.toggle('active'); const icon = this.querySelector('i'); if (navLinks.classList.contains('active')) { icon.classList.remove('fa-bars'); icon.classList.add('fa-times'); } else { icon.classList.remove('fa-times'); icon.classList.add('fa-bars'); } }); } const navLinkItems = document.querySelectorAll('.nav-links a'); navLinkItems.forEach(link => { link.addEventListener('click', function () { if (window.innerWidth <= 768) { navLinks.classList.remove('active'); const icon = hamburger.querySelector('i'); icon.classList.remove('fa-times'); icon.classList.add('fa-bars'); } }); }); const currentPath = window.location.pathname.split('/').pop() || 'index.html'; navLinkItems.forEach(link => { if (link.getAttribute('href') === currentPath) { link.classList.add('active'); } }); document.querySelectorAll('a[href^="#"]').forEach(anchor => { anchor.addEventListener('click', function (event) { const hash = this.hash; if (hash !== "") { event.preventDefault(); const target = document.querySelector(hash); if (target) { const offsetTop = target.offsetTop - 80; window.scrollTo({ top: offsetTop, behavior: 'smooth' }); } } }); }); const whatsappButton = document.createElement('a'); whatsappButton.href = 'https://wa.me/919822581297'; whatsappButton.className = 'whatsapp-float'; whatsappButton.target = '_blank'; whatsappButton.setAttribute('aria-label', 'Contact us on WhatsApp'); whatsappButton.innerHTML = '<i class="fab fa-whatsapp whatsapp-icon"></i>'; document.body.appendChild(whatsappButton); const scrollTopBtn = document.createElement('button'); scrollTopBtn.id = 'scrollTopBtn'; scrollTopBtn.title = 'Go to top'; scrollTopBtn.setAttribute('aria-label', 'Scroll to top'); scrollTopBtn.innerHTML = '<i class="fas fa-arrow-up"></i>'; document.body.appendChild(scrollTopBtn); window.addEventListener('scroll', function () { if (window.pageYOffset > 300) { scrollTopBtn.style.display = 'block'; } else { scrollTopBtn.style.display = 'none'; } }); scrollTopBtn.addEventListener('click', function () { window.scrollTo({ top: 0, behavior: 'smooth' }); return false; }); const galleryGrid = document.querySelector('.gallery-grid'); if (galleryGrid) { const lightboxHTML = ` <div id="lightbox" class="lightbox"> <span class="close-lightbox">&times;</span> <img class="lightbox-content" id="lightbox-img"> <div id="lightbox-caption"></div> </div> `; document.body.insertAdjacentHTML('beforeend', lightboxHTML); const lightbox = document.getElementById('lightbox'); const lightboxImg = document.getElementById('lightbox-img'); const lightboxCaption = document.getElementById('lightbox-caption'); const closeLightbox = document.querySelector('.close-lightbox'); document.addEventListener('click', function (e) { if (e.target.matches('.gallery-item img')) { lightbox.style.display = 'block'; lightboxImg.src = e.target.src; lightboxCaption.textContent = e.target.alt; } }); closeLightbox.addEventListener('click', function () { lightbox.style.display = 'none'; }); lightbox.addEventListener('click', function (e) { if (e.target !== lightboxImg) { lightbox.style.display = 'none'; } }); } });